import React, { useState } from 'react';
import { HabitProvider, useHabits } from './context/HabitContext';
import Header from './components/Header';
import HabitForm from './components/HabitForm';
import HabitCard from './components/HabitCard';
import Calendar from './components/Calendar';
import Stats from './components/Stats';
import { Habit, HabitFormData } from './types';
import { getToday } from './utils/dateUtils';

type View = 'habits' | 'calendar' | 'stats';

const AppContent: React.FC = () => {
  const { habits, addHabit, updateHabit } = useHabits();
  const [showForm, setShowForm] = useState(false);
  const [editingHabit, setEditingHabit] = useState<Habit | null>(null);
  const [currentView, setCurrentView] = useState<View>('habits');
  const [selectedDate, setSelectedDate] = useState(getToday());

  const handleAddHabit = () => {
    setEditingHabit(null);
    setShowForm(true);
  };

  const handleEditHabit = (habit: Habit) => {
    setEditingHabit(habit);
    setShowForm(true);
  };

  const handleFormSubmit = (habitData: HabitFormData) => {
    if (editingHabit) {
      updateHabit(editingHabit.id, habitData);
    } else {
      addHabit(habitData);
    }
    setShowForm(false);
    setEditingHabit(null);
  };

  const handleFormCancel = () => {
    setShowForm(false);
    setEditingHabit(null);
  };

  const getCompletedHabitsForDate = (date: string) => {
    return habits.filter(habit => habit.completedDates.includes(date));
  };

  const completedToday = getCompletedHabitsForDate(selectedDate);

  return (
    <div className="min-h-screen bg-gray-50">
      <Header onAddHabit={handleAddHabit} />

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Navigation */}
        <div className="mb-8">
          <nav className="flex space-x-8">
            <button
              onClick={() => setCurrentView('habits')}
              className={`px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                currentView === 'habits'
                  ? 'bg-primary-100 text-primary-700'
                  : 'text-gray-500 hover:text-gray-700 hover:bg-gray-100'
              }`}
            >
              Habits
            </button>
            <button
              onClick={() => setCurrentView('calendar')}
              className={`px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                currentView === 'calendar'
                  ? 'bg-primary-100 text-primary-700'
                  : 'text-gray-500 hover:text-gray-700 hover:bg-gray-100'
              }`}
            >
              Calendar
            </button>
            <button
              onClick={() => setCurrentView('stats')}
              className={`px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                currentView === 'stats'
                  ? 'bg-primary-100 text-primary-700'
                  : 'text-gray-500 hover:text-gray-700 hover:bg-gray-100'
              }`}
            >
              Statistics
            </button>
          </nav>
        </div>

        {/* Content */}
        {currentView === 'habits' && (
          <div className="space-y-6">
            {habits.length === 0 ? (
              <div className="text-center py-12">
                <div className="mx-auto w-24 h-24 bg-gray-100 rounded-full flex items-center justify-center mb-4">
                  <svg className="w-12 h-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                  </svg>
                </div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">No habits yet</h3>
                <p className="text-gray-500 mb-4">Start building better habits by adding your first one.</p>
                <button
                  onClick={handleAddHabit}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700"
                >
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                  Add Your First Habit
                </button>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {habits.map(habit => (
                  <HabitCard
                    key={habit.id}
                    habit={habit}
                    onEdit={handleEditHabit}
                  />
                ))}
              </div>
            )}
          </div>
        )}

        {currentView === 'calendar' && (
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            <div className="lg:col-span-2">
              <Calendar
                selectedDate={selectedDate}
                onDateSelect={setSelectedDate}
              />
            </div>
            <div className="space-y-6">
              <div className="bg-white rounded-lg shadow-md p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">
                  Habits for {new Date(selectedDate).toLocaleDateString()}
                </h3>
                {completedToday.length > 0 ? (
                  <div className="space-y-2">
                    {completedToday.map(habit => (
                      <div key={habit.id} className="flex items-center gap-2">
                        <div
                          className="w-3 h-3 rounded-full"
                          style={{ backgroundColor: habit.color }}
                        />
                        <span className="text-sm text-gray-700">{habit.title}</span>
                        <span className="text-xs text-success-600 font-medium">✓</span>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-gray-500 text-sm">No habits completed on this date.</p>
                )}
              </div>
            </div>
          </div>
        )}

        {currentView === 'stats' && <Stats />}
      </main>

      {/* Habit Form Modal */}
      {showForm && (
        <HabitForm
          onSubmit={handleFormSubmit}
          onCancel={handleFormCancel}
          habit={editingHabit || undefined}
          isEditing={!!editingHabit}
        />
      )}
    </div>
  );
};

const App: React.FC = () => {
  return (
    <HabitProvider>
      <AppContent />
    </HabitProvider>
  );
};

export default App;
